workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*
import java.rmi.server.*;

class MeterListController
{

    @Service("WaterworksMeterService")
    def meterSvc;

    def list;
    def title;
    def inventorylist;

    def handler = {
        init();
        meterModel.reload();
    }

    void init(){
        list = meterSvc.getList(['searchtext':'%']);
        inventorylist = [];
        title = "Meters";
    }

    def create(){
        return Inv.lookupOpener("meter:create",[handler:handler]);
    }

    def meter;
    def meterModel = [
        fetchList : {
            return list;
        }
    ] as BasicListModel;

    def inventory;
    def inventoryModel = [
        fetchList : {
            return inventorylist;
        }
    ] as BasicListModel;

}
