import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*

class RateListController
{

    @Service("WaterworksRateService")
    def rateSvc;

    def list;
    def title;
    def rates;

    def handler = {
        refresh();
    }

    void init(){
        title = "Water Rates";
        rates = [];
        list = rateSvc.getList();
        list.each{
            def rate = [:];
            rate.objid = it.objid;
            rate.metersize = it.metersize;
            int index = 1;
            it.rates.each{
                if(index == 1) rate.bracket1 = it.from + "-" + it.to + "     " + it.type + "     " + it.amount;
                if(index == 2) rate.bracket2 = it.from + "-" + it.to + "     " + it.type + "     " + it.amount;
                if(index == 3) rate.bracket3 = it.from + "-" + it.to + "     " + it.type + "     " + it.amount;

                index++;
            }
            rates << rate;
        }
    }

    void refresh(){
        init();
        rateModel.reload();
    }

    def create(){
        return Inv.lookupOpener("waterrate:create",[handler:handler]);
    }

    def open(){
        return Inv.lookupOpener("waterrate:open",[entity:rate,handler:handler]);
    }

    def rate;
    def rateModel = [
        fetchList : {
            return rates;
        },
        fireSelectedItemChanged: {
            rate = rateModel.getSelectedValue();
        }
    ] as BasicListModel;

}
