import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*
import java.rmi.server.UID;

class RouteListController
{

    @Service('WaterworksRouteService')
    def routeSvc;

    @Binding
    def binding;

    def entity;
    def title;
    def routes;

    void init(){
        entity = [:];
        routes = routeSvc.getList(['searchtext':'%']);
        title = "Routes";
    }

    void refresh(){
        init();
        routeModel.reload();
    }

    def open(){
        return Inv.lookupOpener("waterworksroute:open",[entity:route]);
    }

    def create(){
        return Inv.lookupOpener("waterworksroute:create");
    }

    def route;
    def routeModel = [
        fetchList : {
            return routes;
        },
        fireSelectedItemChanged : {
            route = routeModel.getSelectedValue();
        }
    ] as BasicListModel;

    @PropertyChangeListener
    def listener = [
        "route.path" : {o ->
            println o;
            binding.refresh("route.*");
        }
    ];

}
