import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class RevenueItemEntryController {        

    def entity;
    def handler
    def mode

    void init() {
        entity = [:]
        mode = 'create'
    }

    void open() {
        mode = 'open'
    }

    def doOk() {
        if( handler) handler(entity);
        return "_close";
    }

    def doCancel() {
        return "_close";
    }

}
