workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import java.rmi.server.*;
import com.rameses.osiris2.reports.*;

class  BusinessBillingReportController  {

    @Service("ReportParameterService") 
    def paramsvc

    @Service("BusinessApplicationBillingService") 
    def billingSvc;

    def entity;
    def data;
    def title = "Business Billing"
    def reportPath = "com/rameses/gov/etracs/bpls/reports/billing/"

    void viewReport() {
        data = billingSvc.getReport([applicationid:entity.objid]); 
        report.viewReport();
    } 

    def report = [
        getReportName : { return reportPath + "main.jasper" },
        getReportData : { return  data },
        getParameters : { return paramsvc.getStandardParameter() },
        getSubReports : { return [
                new SubReport("billitem", reportPath + "billitem.jasper"),
                new SubReport("billpayment", reportPath + "billpayment.jasper"),
            ] as SubReport[];
        }
    ] as ReportModel
 }
