import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.common.*;
import java.rmi.server.*
import com.rameses.util.*;

class  BusinessApplicationAssessmentController {

    @Binding
    def binding;

    @Caller
    def caller;

    @Script("BusinessAssessmentInfoUtil")
    def assessmentInfo;

    @Service("BusinessApplicationService")
    def appSvc;

    String title = "Assessment";
    def task;
    def entity;
    String entityName = "business_application:assessment";

    public void load() {
        assessmentInfo.analyzer = false;
        assessmentInfo.load();
    }

    public void save() {
        appSvc.saveAssessment( entity );
    }

}
