import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.common.*;
import java.rmi.server.*
import com.rameses.util.*;


class  BusinessApplicationOptionInfoController {

    def entity;
    def task;

    String title = "General Information"

    String entityName = "business_application:section:general";

    def lobModel = [
        fetchList: { o->
            return entity.lobs;
        }
    ] as BasicListModel;

    def appInfoModel = [
        fetchList: { o->
            return entity.appinfos;
        }
    ] as BasicListModel;

    def viewBusiness() {
       return Inv.lookupOpener( "business:open", [entity:entity.business] );
    }

}
