import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;


class ReportRoaController extends com.rameses.gov.etracs.rpt.report.AsyncReportController
{
    @Service('RPTReportROAService') 
    def svc;

    @Service('DateService')
    def dtSvc; 

    @Service('BarangayService')
    def brgySvc;

    @Service('PropertyClassificationService')
    def pcSvc;

    String title = 'Record of Assessment';

    String reportName = 'com/rameses/gov/etracs/rpt/report/rptroa.jasper';

    void buildReportData(entity, asyncHandler){
        svc.getROA(entity, asyncHandler);
    }

    Map getParameters(){
        return data.parameters;
    }    

    def getPropertyClassifications(){
        return pcSvc.getList([:]);
    }

    def formControl = [
        getFormControls: {
            return [
                new FormControl( "number", [captionWidth:100, caption:'Year', name:'entity.year', required:true, preferredSize:'60,19', pattern:'####']),
                new FormControl( "combo", [captionWidth:100,caption:'Quarter', name:'entity.qtr', items:'quarters', required:true, immediate:true, preferredSize:'100,19']),
                new FormControl( "combo", [captionWidth:100, caption:'Month', name:'entity.month', items:'months', depends:'entity.qtr', expression:'#{item.name}', dynamic:true, preferredSize:'100,19']),
                new FormControl( "combo", [captionWidth:100, caption:'LGU', name:'entity.lgu', required:true, items:'lgus', expression:'#{item.name}']),
                new FormControl( "combo", [captionWidth:100, caption:'Barangay', name:'entity.barangay', required:true, items:'barangays', expression:'#{item.name}', depends:'entity.lgu', dynamic:true]),
                new FormControl( "combo", [captionWidth:100, caption:'Classification', name:'entity.classification', items:'propertyClassifications', required:true, expression:'#{item.name}', dynamic:true, preferredSize:'250,19']),                
            ]    
        },
   ] as FormPanelModel;

}
