workunit.code:
import com.rameses.rcp.annotations.* 
import com.rameses.rcp.common.* 
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class SubdivisionInitController
{
    @Binding
    def binding;

    @Service('SubdivisionService')
    def svc;

    def entity = [:]

    String getTitle(){
        return 'Subdivision: Initial';
    }

    void init(){
    }

    def getLookupFaas(){
        return InvokerUtil.lookupOpener('faas:lookup', [
            onselect: {
                if (it.state == 'CANCELLED')
                    throw new Exception('FAAS has already been cancelled.');
                if (it.state != 'CURRENT')
                    throw new Exception('Cannot process record. The FAAS is not yet current.')

                 entity.motherfaas = it;
                 binding.refresh('.*');
            },

            enempty :{ entity.motherfaas = null; }
        ])
    }


    def process(){
        entity.objid = 'SD' + new java.rmi.server.UID();
        entity.filetype = 'subdivision';
        def newentity = svc.createSubdivision(entity)
        return InvokerUtil.lookupOpener('subdivision:open', [entity:newentity]);
    }

}
