import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import java.rmi.server.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;
import com.rameses.osiris2.reports.*;

class LiquidationReportByFundController extends com.rameses.etracs.shared.ReportController {

    @Binding
    def binding;

    @Service("LiquidationReportService")
    def svc

    @Service("ReportParameterService")
    def paramsvc

    String title = "Liquidation Report By Fund";

    String reportpath = "com/rameses/gov/treasury/liquidation/reports/"
    String reportName = reportpath + 'reportbyfund.jasper' 

    def fundlist 
    def fund 

    def initReport() {
        fundlist = svc.getFundlist( entity.objid ); 
        setFund( fundlist[0] );
    }

    void setFund(def fund ){
        this.fund = fund 

        buildReport()
        if( binding) binding.refresh()
    }

    def getReportData(){
        return svc.getReportByFund( [ liquidationid:entity.objid, fundname:this.fund.fundid ])
    }

    SubReport[] getSubReports() {
       return [ 
            new SubReport("LiquidationReportBItem", reportpath + "reportbyfunditem.jasper"),
        ] as SubReport[];      
    }


}
