workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;


class DailyCollectionController extends com.rameses.etracs.shared.ReportController 
{
    @Service('CollectorReportService') 
    def svc

    @Service('UserRoleService')
    def userroleSvc

    String title = 'Daily Collection Report'
    String reportpath = 'com/rameses/gov/treasury/report/'
    String reportName = reportpath + 'dailycollectionsummaryreport.jasper'

    def collectors = []

    def initReport(){
        def userlist = userroleSvc.getUsers([domain:'TREASURY', roles:'COLLECTOR']); 
        collectors = userlist.collect{[ 
            objid:it.objid, name:it.name, title:it.title, 
            fullname: it.lastname +', '+ it.firstname +' '+ (it.middlename? it.middlename: '')  
        ]}
        collectors.unique();
        return 'default'
    }


    def data
    def getReportData(){
        data = svc.generateDailyCollectionReport(entity);
        return data.items
    }

    def formControl = [
        getFormControls: {
            return [
                new FormControl( "integer", [caption:'Year', name:'entity.year', required:true, preferredSize:'100,19', captionWidth:100]), 
                new FormControl( "combo", [caption:'Month', name:'entity.month', items:'months', expression:'#{item.name}', preferredSize:'100,19', captionWidth:100, required:true]),
                new FormControl( "combo", [caption:'Collector', name:'entity.collector', required:true, items:'collectors', expression:'#{item.fullname}', preferredSize:'0,19', captionWidth:100]),
            ]    
        }
   ] as FormPanelModel;


   Map getParameters(){
        return data.header
    }

    List getMonths(){
        return dtSvc.getMonths();
    }
}
